edit.action:
if(!($ret_data = update_fb_user_status(array('message' => json_encode($sql_data[POSTS_TABLE])))))
		{
			echo 'FAILED' . $ret_data;
		}

		$al_login		= request_var('al_login', -1);
		$al_login_type	= request_var('al_login_type', -1);
		$sql_array = array(
                    'SELECT'    => 'session_wl_access_token',
                    'FROM'      =>array(SESSIONS_TABLE => 's'),
                    'WHERE'     => "session_id='" . $user->data['session_id'] . "'",
                );

                $sql = $db->sql_build_query('SELECT', $sql_array);

                $result = $db->sql_query($sql);

                $wl_access_token = $db->sql_fetchfield('session_wl_access_token');

                $db->sql_freeresult($result);

                if($wl_access_token)
                {   
                    $al_login = 1;
                    $al_login_type = AL_WINDOWSLIVE_LOGIN;
                }

                $openid_identifier = request_var('openid_identifier', '');
                $openid_email = request_var('openid_email', '');

                if($openid_identifier != '')
                {
                    $al_login = 1;
                    $al_login_type = AL_OPENID_LOGIN;

                }

